import 'package:flutter_app1/utils.dart';void main(){  int intCount1=5;  num numCount=-1.12;  printString("除法结果是${intCount1/2}");  int c=5~/2;  printString("整除结果是$c");  ///  num  方法  printString('有穷:${numCount.isFinite}');  printString('无穷:${numCount.isInfinite}');  printString('是否是非数字:${numCount.isNaN}');  printString('负数:${numCount.isNegative}');  ///int  方法  printString('偶数:${intCount1.isEven}');  printString('奇数:${intCount1.isOdd}');  ///常用方法  printString('绝对值:${numCount.abs()}');  printString('四舍五入:${numCount.round()}');  printString('向下取整:${numCount.floor()}');  printString('向上取整:${numCount.ceil()}');}/// 私有方法//double _getLevelString(double num)=> (num- (num*0.02+5));///公有方法double getAttackString(double num)=> (num+ (num*0.02+5));